import React, { Component } from "react";
import {MDBInput} from "mdbreact"

export default class CardAdder extends Component {

    constructor(props) {
        super(props);
        this.state = {
            last_4: "0000",
            brand: "jcb",
            month: 1,
            year: 2019
        }
        this.generateMonthOption = this.generateMonthOption.bind(this);
    }

    generateMonthOption() {
        var lis = [];
        for (var i = 1; i < 13; i++) {
            lis.push(<option value={i}>{i}</option>);
        }
        return lis;
    }

    generateYearOption(){
        var lis = [];
        for (var i = 2018; i < 2040; i++) {
            lis.push(<option value={i}>{i}</option>);
        }
        return lis;
    }

    render() {
        return (
            <div>
                
                <MDBInput label="Last 4 numbers of your card" />

                <div style={{ width: '45%', display: 'inline-block' }}>
                    <select className="browser-default custom-select">
                        <option>Expiration Month</option>
                        {this.generateMonthOption()}
                    </select>
                </div>
                
                <div style={{ width: '45%', display: 'inline-block', marginLeft:'20px'}}>
                    <select className="browser-default custom-select">
                        <option>Expiration Year</option>
                        {this.generateYearOption()}
                    </select>
                </div>
            </div>
        );
    }
}
