import React from 'react';
import {
    Carousel,
    CarouselItem,
    CarouselControl,
    CarouselIndicators,
    CarouselCaption
} from 'reactstrap';
import slide1 from './CarrouselSlide/slide1.jpg'

const fields = [
    {
        src: slide1,
        altText: 'slide1',
        header: 'Payer en ligne',
        caption: 'Avec notre application, Payer ou Recevez de l\'argent de la part de vos amis'
    }
];

export default class HomeCarrousel extends Component {

    constructor(props) {
        super(props);
        this.state = { active: 0 };
        this.next = this.next.bind(this);
        this.previous = this.previous.bind(this);
        this.goTo = this.goTo.bind(this);
        this.onExiting = this.onExiting.bind(this);
        this.onExited = this.onExited.bind(this);
    }

    onExiting() {
        this.animating = true;
    }

    onExited() {
        this.animating = false;
    }

    next() {
        if (this.animating) return;
        const nextIndex = this.state.active === fields.length - 1 ? 0 : this.state.active + 1;
        this.setState({ active: nextIndex });
    }

    previous() {
        if (this.animating) return;
        const nextIndex = this.state.active === 0 ? fields.length - 1 : this.state.active - 1;
        this.setState({ active: nextIndex });
    }

    goTo(newIndex) {
        if (this.animating) return;
        this.setState({ active: newIndex });
    }

    render() {
        const { active } = this.state;

        const slides = fields.map((field) => {
            return (
                <CarouselItem
                    onExiting={this.onExiting}
                    onExited={this.onExited}
                    key={field.src}
                >
                    <img src={field.src} alt={field.altText} />
                    <CarouselCaption captionText={field.caption} captionHeader={field.header} />
                </CarouselItem>
            );
        });

        return (
            <Carousel active={active} next={this.next} previous={this.previous}>
                <CarouselIndicators slides={slides} active={active} onClickHandler={this.goToIndex} />
                {slides}
                <CarouselControl direction="prev" directionText="Previous" onClickHandler={this.previous} />
                <CarouselControl direction="next" directionText="Next" onClickHandler={this.next} />
            </Carousel>
        );
    }
}
