import React, { Component } from "react";
import { MDBCarousel, MDBCarouselInner, MDBCarouselItem, MDBView, MDBContainer, MDBMask, MDBCarouselCaption } from
    "mdbreact";

import slide1 from './CarrouselSlide/slide1.jpg';
import slide2 from './CarrouselSlide/slide2.jpg';


class HomeCarousel extends Component {
    render() {
        return (
            <MDBContainer style={{ maxWidth: '100%' }} className="px-0">
                <MDBCarousel activeItem={1} length={2} showControls={true} showIndicators={true} className="z-depth-1" slide>
                    <MDBCarouselInner>
                        <MDBCarouselItem itemId="1">
                            <MDBView>
                                <img className="d-block w-100" src={slide1} alt="First slide" />
                                <MDBMask overlay="black-light" />
                            </MDBView>
                            <MDBCarouselCaption>
                                <h3 className="h3-responsive">Online Payments with WaterMelon !</h3>
                                <p>WaterMelon is an online micropayments platform that allows you to manage a virtual wallet. Use our tools to manage payments between friends, family or anyone! </p>
                            </MDBCarouselCaption>
                        </MDBCarouselItem>
                        <MDBCarouselItem itemId="2">
                            <MDBView>
                                <img className="d-block w-100" src={slide2} alt="Second slide" />
                                <MDBMask overlay="black-light" />
                            </MDBView>
                            <MDBCarouselCaption>
                                <h3 className="h3-responsive">Pay Fast with Friends !</h3>
                                <p>Our solution is really fast and designed to do micro-payments to your friends !</p>
                            </MDBCarouselCaption>
                        </MDBCarouselItem>
                    </MDBCarouselInner>
                </MDBCarousel>
            </MDBContainer>
        );
    }

}


export default HomeCarousel;
