import React, { Component } from "react";
import { getSessionUser } from "../../Database/DatabaseSession";
import {
    MDBRow, MDBCol, MDBBtn, MDBCard, MDBCardBody, MDBCardHeader, MDBCardFooter,
    MDBCardTitle
} from "mdbreact";
export default class AccountEditor extends Component {

    constructor(props) {
        super(props);

        let user = getSessionUser();
        this.state = {
            first_name: user.first_name,
            last_name: user.last_name,
            email: user.email,
            password: user.password
        }
    }

    render() {
        return (
            <form>
                <MDBCard className="text-center" >

                    <MDBCardHeader style={{ backgroundColor: "inherit" }}>
                        <MDBCardTitle style={{ fontSize: '36px', marginTop: '20px', marginBottom: '20px' }}>My Account Informations</MDBCardTitle>
                    </MDBCardHeader>
                    <MDBCardBody style={{ margin: '50px' }}>



                    </MDBCardBody>
                    <MDBCardFooter style={{ backgroundColor: "inherit" }}>
                        <MDBRow middle>
                            <MDBCol md="12">
                                <MDBBtn outline color="success" size="lg" style={{ marginTop: '30px' }} onClick={() => this.showAddingCard()}>Save Changes</MDBBtn>
                            </MDBCol>
                        </MDBRow>
                    </MDBCardFooter>
                </MDBCard>
            </form>
        );
    }
}
