import React, { Component } from "react";
import { MDBRow, MDBCol, MDBListGroup, MDBBtn, MDBListGroupItem, MDBCard, MDBCardBody,
    MDBCardTitle, MDBCardText, MDBContainer } from "mdbreact";
import cb from './Pictures/cb.png';
import visa from './Pictures/visa.png';
import mastercard from './Pictures/mastercard.png';

export default class WalletContent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            modal: false
        };
        this.toggle = this.toggle.bind(this);
        this.test = this.test.bind(this);
    }

    toggle() {
        this.setState({
            modal: !this.state.modal
        });
    }

    test(){
        return 'active';
    }

    render() {
        return (
            <div>
                <MDBContainer  style={{marginBottom:'50px', marginTop:'50px'}}>
                    <h1 className="text-center" style={{fontSize: '40px', fontWeight: 'bold' }}>My Bank Manager</h1>
                </MDBContainer>
                <MDBRow className="text-align-center" >
                    <MDBCol md="5" style={{ paddingLeft: '5%', paddingRight: '5%', paddingBottom: '100px', width: '100%' }}>
                        <MDBCard className="text-center" >
                            <MDBCardBody>

                                <MDBCardTitle style={{ fontSize: '36px' }}>Choose a Card</MDBCardTitle>
                                <MDBCardText className=" text-justify" style={{ fontSize: '18px' }}>
                                    Select a Card among all the cards associated to your Watermelon Account. You may use this card to make deposit to your bank account as well as withdrawals 
                                </MDBCardText>

                                <MDBListGroup >
                                    <MDBListGroupItem className={this.test()} style={{ paddingTop: '25px', paddingBottom: '25px' }}>
                                        <div className="justify-content-between">
                                            <img src={cb} alt="" style={{ width: '100%', maxWidth: '80px', display:'inline' }}></img>
                                            <h3 className="mb-1" style={{display:'inline', marginLeft:'20px'}}>Card : 1234-****-****-1234</h3>
                                        </div>
                                    </MDBListGroupItem>
                                    <MDBListGroupItem hover style={{ paddingTop: '25px', paddingBottom: '25px' }}>
                                        <div className="justify-content-between">
                                            <img src={mastercard} alt="" style={{ width: '100%', maxWidth: '80px', display:'inline' }}></img>
                                            <h3 className="mb-1" style={{display:'inline', marginLeft:'20px'}}>Card : 1234-****-****-1234</h3>
                                        </div>
                                    </MDBListGroupItem>
                                    <MDBListGroupItem hover style={{ paddingTop: '25px', paddingBottom: '25px' }}>
                                        <div className="justify-content-between">
                                            <img src={visa} alt="" style={{ width: '100%', maxWidth: '80px', display:'inline' }}></img>
                                            <h3 className="mb-1" style={{display:'inline', marginLeft:'20px'}}>Card : 1234-****-****-1234</h3>
                                        </div>
                                    </MDBListGroupItem>

                                </MDBListGroup>

                                <MDBBtn outline color="dark" size="lg" style={{ marginTop: '30px' }}>
                                    Show Older Activity
                                </MDBBtn>

                            </MDBCardBody>
                        </MDBCard>
                    </MDBCol>


                    <MDBCol md="1" className="text-center">
                    </MDBCol>


                    <MDBCol md="6" style={{ paddingRight: '100px', paddingLeft: '40px', paddingBottom: '100px' }}>
                        
                    </MDBCol>
                </MDBRow>
            </div>
        );
    }
}
