import React, { Component } from "react";
import { MDBInput, MDBModal, MDBModalHeader, MDBModalBody, MDBModalFooter, MDBBtn } from "mdbreact"

export default class CardAdder extends Component {

    constructor(props) {
        super(props);
        this.toggled = true;
        this.toggleAddingCard = null;
        this.state = {
            last_4: "0000",
            brand: "jcb",
            month: 1,
            year: 2019
        }
        this.generateMonthOption = this.generateMonthOption.bind(this);
        this.generateYearOption = this.generateYearOption.bind(this);
        this.updateLast4 = this.updateLast4.bind(this);
        this.updateBrand = this.updateLast4.bind(this);
        this.updateMonth = this.updateLast4.bind(this);
        this.updateYear = this.updateLast4.bind(this);
    }

    submit = event => {
        event.preventDefault();
        event.target.className += " was-validated";
    };

    updateLast4(event) {
        this.setState({ last_4:  event.target.value});
    };

    updateBrand(event){
        this.setState({ brand:  event.target.value});
    };

    updateMonth(event){
        this.setState({ month:  event.target.value});
    }

    updateYear(event){
        this.setState({ year:  event.target.value});
    }

    generateMonthOption() {
        var lis = [];
        for (var i = 1; i < 13; i++) {
            lis.push(<option value={i}>{i}</option>);
        }
        return lis;
    }

    generateYearOption() {
        var lis = [];
        for (var i = 2018; i < 2040; i++) {
            lis.push(<option value={i}>{i}</option>);
        }
        return lis;
    }

    render() {
        return (
            <MDBModal centered isOpen={this.props.toggled} toggle={() => this.props.toggleAddingCard()}>
                <MDBModalHeader toggle={() => this.props.toggleAddingCard()}>Add a new Card</MDBModalHeader>
                <MDBModalBody>
                    <form className="needs-validation" onSubmit={this.submitHandler} noValidate>

                        <MDBInput value={this.state.last_4} onChange={this.updateLast4} type="number" className="form-control" label="Last Four numbers of your card" required/>
                        {this.state.last_4}
                        <div style={{ marginBottom: '15px' }} onChange={this.updateBrand}>
                            <select className="browser-default custom-select">
                                <option>Card Brand</option>
                                <option value='jcb'>CB</option>
                                <option value='master_card'>MasterCard</option>
                                <option value='visa'>Visa</option>
                                <option value='american_express'>American Express</option>
                                <option value='union_pay'>Union Pay</option>
                            </select>
                        </div>

                        <div style={{ width: '45%', display: 'inline-block' }}>
                            <select className="browser-default custom-select" onChange={this.updateMonth}>
                                <option>Expiration Month</option>
                                {this.generateMonthOption()}
                            </select>
                        </div>

                        <div style={{ width: '45%', display: 'inline-block', marginLeft: '20px' }}>
                            <select className="browser-default custom-select" onChange={this.updateYear}>
                                <option>Expiration Year</option>
                                {this.generateYearOption()}
                            </select>
                        </div>
                    </form>
                </MDBModalBody>
                <MDBModalFooter>
                    <MDBBtn color="secondary" onClick={() => this.props.toggleAddingCard()}>Close</MDBBtn>
                    <MDBBtn color="primary" type="submit">Proceed</MDBBtn>
                </MDBModalFooter>
            </MDBModal>

        );
    }
}
